import React from "react";
import { useHistory } from "react-router-dom"; // Import useHistory hook
import "./sidebar.css";

// SidebarPoint component represents a single point/button in the sidebar
function SidebarPoint(props) {
  const history = useHistory(); // Initialize useHistory hook
  const { name, url } = props; // Destructure the 'name' and 'url' props

  // Function to handle button click and redirect to the specified URL
  const handleButtonClick = () => {
    history.push(url); // Redirect to the specified URL
  };

  // Render a button representing a sidebar point with its name
  return (
    <button onClick={handleButtonClick} className="sidebarPointContainer">
      <h1 className="sidebarPointHeading">{name}</h1>
    </button>
  );
}

// Sidebar component represents the entire sidebar containing multiple points/buttons
function Sidebar() {
  // Render sidebar points/buttons using SidebarPoint component
  return (
    <div className="sidebarContainer">
      <SidebarPoint name="Home" url="/home" />
      <SidebarPoint name="Patients" url="/patients" />
      <SidebarPoint name="Messages" url="/messages" />
      <SidebarPoint name="Documents" url="/documents" />
      <SidebarPoint name="Doctor stats" url="/doctor-stats" />
      <SidebarPoint name="Hire doctor" url="/hire-doctor" />
      <SidebarPoint name="Hire accountant" url="/hire-accountant" />
      <SidebarPoint name="Finance" url="/finance" />
      <SidebarPoint name="Settings" url="/settings" />
    </div>
  );
}

export default Sidebar;
